;Monta a sequência a ser repetida pelo usuário
BaseSequence:
    .loopi:
        lodsb                                   ;Carrega em al o caractere atual da sequencia
        mov bl, al                              ;Move este caractere para bl  

        cmp bl, 0                               ;Compara o caractere com o final da string ('\0' = 0 = NULL)
        je .done                                ;Se al = 0, é o fim da sequencia base da rodada

        call ColorsCompare                      ;Define que cor do puzzle deve piscar
        call delay100ms                         ;Espera um tempo entre a alternância de cores

        jmp .loopi                              ;Retornamos ao loop

    .done:
ret

;Compara a letra digitada pelo jogador com a letra atual da sequência
LettersCompare:
    ;Se bl = al, o jogador digitou a letra correta
    cmp bl, al
    je .equal

    ;Caso contrário...
    
    ;Subtraímos 32 de bl para pegar sua letra maiúscula
    sub bl, 48

    ;E comparamos novamente
    cmp bl, al
    je .equal

    ;Caso sejam diferentes
    jmp .notEqual

    .equal:
        ;dx = 1 (FLAG = TRUE)
        mov dx, 1 
        jmp .done

    .notEqual:
        ;dx = 0 (FLAG = FALSE)
        mov dx, 0
        jmp .done

    .done:
ret

;Pega a sequência digitada pelo usuário
Player:
    .read:
        lodsb                               ;Carrega em al o caractere atual da sequencia
        mov bl, al                          ;Move este caractere para bl

        cmp al, 0                           ;Se al = 0, ele chegou ao fim da string
        je .passed                          ;Então ele avançou de fase

        ;Caso contrário...
        call getchar                        ;Carrega em al o caracter digitado pelo jogador
        call LettersCompare                 ;Compara se o caractere digitado é igual ao caractere da sequencia
        cmp dx, 0                           ;Compara a flag com 0: se forem iguais, o jogador perdeu o jogo
        je .lose

        ;Caso contrário, mantemos o jogo...
        call ColorsCompare                  ;Define que cor do puzzle deve piscar
        call delay100ms                     ;Intervalo entre a alternância de cores
                

        jmp .read                           ;Caso sejam iguais, voltamos para o 'read' e lemos o próximo caractere 

    .lose:
        ;Flag = FALSE (Usuário perdeu o jogo)
        mov ax, 0
        ret
        
    .passed:
        ;Flag = TRUE (Usuário venceu a rodada)
        mov ax, 1
ret

;Compara as cores de acordo com o char da sequência
ColorsCompare:
    ;Azul
    mov cl, 'b'
    cmp al, cl
    je .blue

    mov cl, 'B'
    cmp al, cl
    je .blue
    
    ;Verde
    mov cl, 'g'
    cmp al, cl
    je .green

    mov cl, 'G'
    cmp al, cl
    je .green

    ;Rosa
    mov cl, 'p'
    cmp al, cl
    je .pink 
    
    mov cl, 'P'
    cmp al, cl
    je .pink 

    ;Vermelho
    mov cl, 'r'
    cmp al, cl
    je .red

    mov cl, 'R'
    cmp al, cl
    je .red


    .blue:
        call PiscaBlue
        jmp .done
        
    .green:
        call PiscaGreen
        jmp .done

    .pink:
        call PiscaPink
        jmp .done

    .red:
        call PiscaRed
        jmp .done

    .done:   
ret

;Pisca o quadrado azul
PiscaBlue:
    call DrawLightBlue
    call delay0.2s
    call DrawBlue
ret

;Pisca o quadrado verde
PiscaGreen:
