ret

;Delay de 600ms
delay0.6s:
    call delay0.3s
    call delay0.3s
ret

;Delay de 100ms
delay100ms:              
  mov cx, 01h
  mov dx, 86a0h
  mov ah, 86h
  int 15h

ret

;Delay de 200ms
delay0.2s:
    call delay100ms
    call delay100ms
ret

;Delay de 300ms
delay0.3s:
    call delay100ms
    call delay100ms
    call delay100ms
    call delay100ms
ret 

;Delay de 1s
delay1s:                 
  mov cx, 0fh
  mov dx, 4240h
  mov ah, 86h
  int 15h
ret

