;Desenha um bloco 100x100
;dx = linha // cx = coluna // janela: 200x320
DrawBox:

    mov bx, dx
    add bx, 100

    .loopLine:

        cmp dx, bx
        je .endLoopLine

        push bx
        mov bx, cx
        add bx, 100

        .loopColumn:

            cmp cx, bx
            je .endLoopColumn

            call print_pixel
            inc cx

            jmp .loopColumn

        .endLoopColumn:

        sub cx, 100
        pop bx

        inc dx
        jmp .loopLine

    .endLoopLine:
    sub dx, 100
ret

;Desenha em toda janela com a cor em al
clean:

    push dx
    push cx

    mov dx, 0

    .loopLine:

        cmp dx, 200
        je .endLoopLine

        mov cx, 0

        .loopColumn:
        
            cmp cx, 320
            je .endLoopColumn

            call print_pixel
            inc cx
            jmp .loopColumn

        .endLoopColumn:

            inc dx
            jmp .loopLine

    .endLoopLine:

        pop cx
        pop dx
ret

